package com.java.practice;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Rahul Automation";
		
		System.out.println(name.charAt(5));
		System.out.println(name.compareTo("Rahul ggf"));
		System.out.println(name.concat(" Studio"));
		System.out.println(name.contains("Automa"));
		System.out.println(name.endsWith("ionh"));
		System.out.println(name.length());
		System.out.println(name.toUpperCase());
		System.out.println(name.toLowerCase());
		System.out.println(name.equalsIgnoreCase("Rahul Automation"));

	}

}
